import React, { useState } from "react";
import {
  FaFileExcel,
  FaImage,
  FaRegCommentDots,
  FaPaperPlane,
} from "react-icons/fa";
import "./App.css";

export default function App() {
  const [excelFile, setExcelFile] = useState(null);
  const [excelFileName, setExcelFileName] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleExcelChange = (e) => {
    const file = e.target.files[0];
    if (file && (file.name.endsWith(".xlsx") || file.name.endsWith(".xls"))) {
      setExcelFile(file);
      setExcelFileName(file.name);
    } else {
      alert("Please upload a valid Excel file (.xlsx or .xls)");
      e.target.value = "";
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!excelFile || !message.trim()) {
      setStatus("Please upload an Excel file and enter a message.");
      return;
    }

    const formData = new FormData();
    formData.append("excel", excelFile);
    if (imageFile) formData.append("image", imageFile);
    formData.append("message", message);

    setIsLoading(true);
    setStatus(""); // Clear previous status

    try {
      const response = await fetch("http://localhost:5000/send", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.status);

      setStatus(data.status);
    } catch (error) {
      console.error("Error:", error);
      setStatus(`❌ Error: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app-container">
      <div className="form-container animate-slide">
        <h1 className="title">📤 Bulk WhatsApp & Email Sender</h1>
        <form onSubmit={handleSubmit} className="form">
          <div className="input-group">
            <label htmlFor="excelFile">
              <FaFileExcel /> Select Excel File
            </label>
            <input
              type="file"
              id="excelFile"
              accept=".xlsx, .xls"
              onChange={handleExcelChange}
              required
            />
            {excelFileName && <small>📁 Selected: {excelFileName}</small>}
          </div>

          <div className="input-group">
            <label htmlFor="imageFile">
              <FaImage /> Select Image (Optional)
            </label>
            <input
              type="file"
              id="imageFile"
              accept="image/*"
              onChange={handleImageChange}
            />
            {imagePreview && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" />
              </div>
            )}
          </div>

          <div className="input-group">
            <label htmlFor="message">
              <FaRegCommentDots /> Enter Your Message
            </label>
            <textarea
              id="message"
              rows="4"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
              placeholder="Enter your message here..."
            ></textarea>
          </div>

          <button type="submit" className="submit-btn" disabled={isLoading}>
            <FaPaperPlane style={{ marginRight: "8px" }} />
            {isLoading ? "Sending..." : "Send Messages"}
          </button>
        </form>

        {status && (
          <div className={`status-message ${status.includes("❌") ? "error" : "success"}`}>
            {status}
          </div>
        )}
      </div>
    </div>
  );
}
