// src/App.jsx
import React, { useState } from "react";
import {
  FaFileExcel, FaImage, FaRegCommentDots,
  FaPaperPlane, FaDownload, FaCheckCircle, FaSpinner
} from "react-icons/fa";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import * as XLSX from "xlsx";
import "./App.css";

export default function App() {
  const [excelFile, setExcelFile] = useState(null);
  const [excelFileName, setExcelFileName] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState("");
  const [logUrl, setLogUrl] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [contactsCount, setContactsCount] = useState(0);
  const [fieldMapping, setFieldMapping] = useState({ Name: false, Email: false, Phone: false });

  const handleExcelChange = async (e) => {
    const file = e.target.files[0];
    if (!file || (!file.name.endsWith(".xlsx") && !file.name.endsWith(".xls"))) {
      alert("Please upload a valid Excel file (.xlsx or .xls)");
      e.target.value = "";
      return;
    }
    setExcelFile(file);
    setExcelFileName(file.name);

    const reader = new FileReader();
    reader.onload = (evt) => {
      const data = new Uint8Array(evt.target.result);
      const workbook = XLSX.read(data, { type: "array" });
      const sheet = workbook.Sheets[workbook.SheetNames[0]];
      const json = XLSX.utils.sheet_to_json(sheet);
      setContactsCount(json.length);

      // Field mapping
      const columns = Object.keys(json[0] || {});
      setFieldMapping({
        Name: columns.includes("Name"),
        Email: columns.includes("Email"),
        Phone: columns.includes("Phone"),
      });
    };
    reader.readAsArrayBuffer(file);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setImageFile(file);
    const reader = new FileReader();
    reader.onloadend = () => setImagePreview(reader.result);
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!excelFile || !message.trim()) {
      toast.error("Please upload an Excel file and enter a message.");
      return;
    }

    const formData = new FormData();
    formData.append("excel", excelFile);
    if (imageFile) formData.append("image", imageFile);
    formData.append("message", message);

    setIsLoading(true);
    setStatus("");
    try {
      const response = await fetch("http://localhost:5000/send", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.status);

      toast.success("✅ Messages sent successfully!");
      setStatus("✅ Messages sent successfully.");
      setLogUrl(data.log_url);

      // Reset
      setExcelFile(null);
      setExcelFileName("");
      setImageFile(null);
      setImagePreview(null);
      setMessage("");
      setContactsCount(0);
      setFieldMapping({ Name: false, Email: false, Phone: false });
    } catch (error) {
      console.error("Error:", error);
      toast.error(`❌ ${error.message}`);
      setStatus(`❌ Error: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app-container">
      <ToastContainer />
      <div className="form-container animate-slide">
        <h1 className="title">📤 Nutrition By Argha</h1>
        <form onSubmit={handleSubmit} className="form">
          <div className="input-group">
            <label htmlFor="excelFile">
              <FaFileExcel /> Select Excel File
            </label>
            <input
              type="file"
              id="excelFile"
              accept=".xlsx, .xls"
              onChange={handleExcelChange}
              required
            />
            {excelFileName && (
              <small>📁 Selected: {excelFileName}</small>
            )}
          </div>

          {contactsCount > 0 && (
            <>
              <div className="info">
                🧾 <strong>Total Contacts:</strong> {contactsCount}
              </div>
              <div className="info">
                ✅ <strong>Detected Fields:</strong>
                {Object.entries(fieldMapping).map(([key, val]) =>
                  val ? ` ${key}` : ""
                )}
              </div>
            </>
          )}

          <div className="input-group">
            <label htmlFor="imageFile">
              <FaImage /> Select Image/PDF (Optional)
            </label>
            <input
              type="file"
              id="imageFile"
              accept="image/*,.pdf"
              onChange={handleImageChange}
            />
            {imagePreview && imageFile?.type?.startsWith("image/") && (
              <div className="image-preview">
                <img src={imagePreview} alt="Preview" />
              </div>
            )}
            {imageFile && imageFile.type === "application/pdf" && (
              <small>📄 PDF file selected: {imageFile.name}</small>
            )}
          </div>

          <div className="input-group">
            <label htmlFor="message">
              <FaRegCommentDots /> Enter Your Message
            </label>
            <textarea
              id="message"
              rows="4"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
              placeholder="Enter your message here..."
            ></textarea>
          </div>

          {message && fieldMapping.Name && (
            <div className="info preview-msg">
              ✉️ Preview: {message.replace("Hello", "Hello Sir/Mam Argha Khawas")}
            </div>
          )}

          <button type="submit" className="submit-btn" disabled={isLoading}>
            {isLoading ? (
              <>
                <FaSpinner className="spinner" /> Sending...
              </>
            ) : (
              <>
                <FaPaperPlane style={{ marginRight: "8px" }} />
                Send Messages
              </>
            )}
          </button>
        </form>

        {status && (
          <div className={`status-banner ${status.startsWith("✅") ? "success" : "error"}`}>
            {status}
            {logUrl && (
              <a href={`http://localhost:5000${logUrl}`} download className="log-link">
                <FaDownload style={{ marginLeft: "8px" }} /> Download Log
              </a>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
