import React, { useState } from "react";
import "./App.css"; // Import the custom CSS file

export default function App() {
  const [excelFile, setExcelFile] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("excel", excelFile);
    formData.append("image", imageFile);
    formData.append("message", message);

    try {
      const response = await fetch("http://localhost:5000/send", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setStatus(data.status);
    } catch (error) {
      console.error("Error:", error);
      setStatus("Something went wrong while sending messages.");
    }
  };

  return (
    <div className="app-container">
      <div className="form-container">
        <h1 className="title">Bulk WhatsApp & Email Sender</h1>
        <form onSubmit={handleSubmit} className="form">
          {/* Excel File Input */}
          <div className="input-group">
            <label htmlFor="excelFile">Select Excel File</label>
            <input
              type="file"
              id="excelFile"
              accept=".xlsx"
              onChange={(e) => setExcelFile(e.target.files[0])}
              required
            />
          </div>

          {/* Image File Input */}
          <div className="input-group">
            <label htmlFor="imageFile">Select Image (Optional)</label>
            <input
              type="file"
              id="imageFile"
              accept="image/*"
              onChange={(e) => setImageFile(e.target.files[0])}
            />
          </div>

          {/* Message Textarea */}
          <div className="input-group">
            <label htmlFor="message">Enter Your Message</label>
            <textarea
              id="message"
              rows="4"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
              placeholder="Enter your message here..."
            ></textarea>
          </div>

          {/* Submit Button */}
          <button type="submit" className="submit-btn">
            Send Messages
          </button>
        </form>

        {/* Status Message */}
        {status && <div className={`status-message ${status.includes('wrong') ? 'error' : 'success'}`}>{status}</div>}
      </div>
    </div>
  );
}
